const fadeIn=function(){const e=document.querySelectorAll(".fade"),t=new TimelineMax;for(i=0;i<e.length;i++)t.from(e,1.5,{transform:"translateY(30px)",autoAlpha:0,ease:Elastic.easeOut.config(1.2,.75)},".5")};fadeIn();const staggerFadeIn=function(){const e=document.querySelectorAll(".recent-post-thumb"),t=new TimelineMax;t.staggerFrom(e,3,{transform:"translateY(50px)",autoAlpha:0,ease:Elastic.easeOut.config(1.2,.75)},.2)},observer=new IntersectionObserver(e=>{e.forEach(e=>{!0===e.isIntersecting&&staggerFadeIn()})}),items=document.querySelectorAll(".recent-post-thumbs");items.forEach((e,t)=>{observer.observe(e,t)});